import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';
import axios from 'axios';

// Register the necessary components for Chart.js
ChartJS.register(
    CategoryScale,
    LinearScale,
    ArcElement,
    Tooltip,
    Legend
);

const TopicDistributionPieChart = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      data: [],
      backgroundColor: [],
      hoverBackgroundColor: []
    }]
  });

   // Define a color mapping for topics
   const topicColors = {
    'Sports': 'rgba(255, 99, 132, 0.6)',
    'Technology': 'rgba(54, 162, 235, 0.6)',
    'Economy': 'rgba(255, 206, 86, 0.6)',
    // Add more topics and their colors here
  };

  useEffect(() => {
    const fetchTopicData = async () => {
      try {
        // Adjusted the endpoint to match the Flask route
        const response = await axios.get('http://127.0.0.1:5000/topic-distribution');
        const data = response.data;
        const topics = Object.keys(data);
        const counts = Object.values(data);

        // Map through the topics to create colors for the chart
        const backgroundColors = topics.map(() => `rgba(${Math.random()*255}, ${Math.random()*255}, ${Math.random()*255}, 0.6)`);
        const hoverBackgroundColors = topics.map(() => `rgba(${Math.random()*255}, ${Math.random()*255}, ${Math.random()*255}, 0.8)`);

        setChartData({
          labels: topics,
          datasets: [{
            data: counts,
            backgroundColor: topics.map(topic => topicColors[topic] || 'rgba(0, 0, 0, 0.6)'), // Default color if topic not in topicColors
            hoverBackgroundColor: topics.map(topic => topicColors[topic] || 'rgba(0, 0, 0, 0.8)')
          }]
        });
      } catch (error) {
        console.error('Error fetching topic distribution data:', error);
      }
    };

    fetchTopicData();
  }, []);

  return <Pie data={chartData} />;
};

export default TopicDistributionPieChart;
