import React, { useState, useEffect, useRef } from 'react';
import './Chatbot.css';
import TopicDistributionPieChart from './TopicDistributionPieChart'; // Ensure this component is properly set up for Chart.js
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend
);

const Chatbot = () => {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);
    const [showChart, setShowChart] = useState(false); // Added state for toggling the chart
    const chatBoxRef = useRef(null);

    useEffect(() => {
        // Scroll to the bottom of the chat box whenever messages change
        if (chatBoxRef.current) {
            chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
        }
    }, [messages]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (input) {
            setMessages([...messages, { text: input, sender: 'user' }]);
            processUserMessage(input);
            setInput('');
        }
    };


    const processUserMessage = async (message) => {
        try {
            // Fetch API call to the Flask server
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ message: message })
            });
    
            // Handling the response from the server
            if (response.ok) {
                const data = await response.json();
                // Initialize an empty array for response messages
                let responseMessages = [];
    
                // If user_topic is present in the response, add it to the messages first
                if (data.user_topic) {
                    responseMessages.push({ text: `Topic: ${data.user_topic}`, sender: 'bot' });
                }
                
                // Add the response summary next
                responseMessages.push({ text: data.response, sender: 'bot' });
    
                // Update the state with the new messages
                setMessages(messages => [...messages, ...responseMessages]);
            } else {
                console.error('Failed to send message:', response.statusText);
            }
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    const toggleChartVisibility = () => { // Function to toggle the visibility of the chart
        setShowChart(!showChart);
    };
    
    const hideChart = () => { // Function to hide the chart
        setShowChart(false);
    };

    return (
        <div className="chat-container">
            <div id="chat-box" ref={chatBoxRef}> 
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}>
                        {message.text}
                    </div>
                ))}
            </div>
            <form onSubmit={sendMessage}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Say something..."
                />
                <button type="submit">Send</button>
            </form>
            <button className="toggle-chart-btn" onClick={toggleChartVisibility}>
                {showChart ? "Hide Chart" : "Show Chart"}
            </button> {/* Button to toggle the chart */}
            {showChart && <TopicDistributionPieChart />} {/* Conditionally render the chart */}
        </div>
    );
};

export default Chatbot;
