import React, { useState, useEffect, useRef } from 'react';
import './Chatbot.css';

const Chatbot = () => {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);
    const chatBoxRef = useRef(null);

    useEffect(() => {
        // Scroll to the bottom of the chat box whenever messages change
        if (chatBoxRef.current) {
            chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
        }
    }, [messages]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (input) {
            setMessages([...messages, { text: input, sender: 'user' }]);
            processUserMessage(input);
            setInput('');
        }
    };

    const processUserMessage = async (message) => {
        try {
            // Fetch API call to the Flask server
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ message: message })
            });
    
            // Handling the response from the server
            if (response.ok) {
                const data = await response.json();
                // Check if user_topic is present in the response and append it to the messages
                let responseMessages = [{ text: data.response, sender: 'bot' }];
                if (data.user_topic) {
                    responseMessages.push({ text: `Topic: ${data.user_topic}`, sender: 'bot' });
                }
                setMessages(messages => [...messages, ...responseMessages]);
            } else {
                console.error('Failed to send message:', response.statusText);
            }
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    return (
        <div className="chat-container">
            <div id="chat-box" ref={chatBoxRef}> 
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}>
                        {message.text}
                    </div>
                ))}
            </div>
            <form onSubmit={sendMessage}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Say something..."
                />
                <button type="submit">Send</button>
            </form>
        </div>
    );
};

export default Chatbot;
