import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';
import axios from 'axios';

const TopicDistributionPieChart = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      data: [],
      backgroundColor: [],
      hoverBackgroundColor: []
    }]
  });

  useEffect(() => {
    const fetchTopicData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/api/topic-distribution');
        const data = response.data;
        const topics = Object.keys(data);
        const counts = Object.values(data);

        setChartData({
          labels: topics,
          datasets: [{
            data: counts,
            backgroundColor: topics.map(() => `rgba(${Math.random()*255}, ${Math.random()*255}, ${Math.random()*255}, 0.6)`),
            hoverBackgroundColor: topics.map(() => `rgba(${Math.random()*255}, ${Math.random()*255}, ${Math.random()*255}, 0.8)`)
          }]
        });
      } catch (error) {
        console.error('Error fetching topic distribution data:', error);
      }
    };

    fetchTopicData();
  }, []);

  return <Pie data={chartData} />;
};

export default TopicDistributionPieChart;
