import React, { useState, useEffect, useRef } from 'react';
import './Chatbot.css';
import TotalQueries from './TotalQueries';
import TopicDistributionPieChart from './TopicDistributionPieChart';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend
);

const Chatbot = () => {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);
    const [showChart, setShowChart] = useState(false); // Added state for toggling the chart
    const [showTotalQueries, setShowTotalQueries] = useState(false); // State to toggle total queries
    const [coverVisible, setCoverVisible] = useState(true); // State for page cover visibility
    const chatBoxRef = useRef(null);
    const inactivityTimer = useRef(null);

    useEffect(() => {
        // Scroll to the bottom of the chat box whenever messages change
        if (chatBoxRef.current) {
            chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
        }
    }, [messages]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (input) {
            setMessages([...messages, { text: input, sender: 'user' }]);
            processUserMessage(input);
            setInput('');
            resetInactivityTimer();
        }
    };

    const resetInactivityTimer = () => {
      // Clear existing timer
      if (inactivityTimer.current) {
          clearTimeout(inactivityTimer.current);
      }

      // Set a new timer
      inactivityTimer.current = setTimeout(() => {
          setMessages(messages => [...messages, { text: "How may I help you?", sender: 'bot' }]);
      }, 3000); // Set to 3000 milliseconds (3 seconds)
  };


    const processUserMessage = async (message) => {
      try {
          // Check if the last message was the follow-up question
          const isRespondingToFollowUp = messages.length > 0 && messages[messages.length - 1].text === "Do you want to ask anything else?";
  
          // Fetch API call to the Flask server
          const response = await fetch('http://127.0.0.1:5000/chat', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify({ message: message, respondingToFollowUp: isRespondingToFollowUp })
          });
  
          // Handling the response from the server
          if (response.ok) {
              const data = await response.json();
              let responseMessages = [];
  
              // If user_topic is present in the response, add it to the messages first
              if (data.user_topic) {
                  responseMessages.push({ text: `Topic: ${data.user_topic}`, sender: 'bot' });
              }
              
              // Add the response summary next
              responseMessages.push({ text: data.response, sender: 'bot' });
  
              // Handle follow_up message if it's present
              if (data.follow_up) {
                  setTimeout(() => {
                      setMessages(messages => [...messages, { text: data.follow_up, sender: 'bot' }]);
                  }, 2000); // Wait for 2 seconds before adding the follow-up message
              }
  
              // Update the state with the new messages
              setMessages(messages => [...messages, ...responseMessages]);
          } else {
              console.error('Failed to send message:', response.statusText);
          }
      } catch (error) {
          console.error('There was a problem with the fetch operation:', error);
      }
      resetInactivityTimer();
  };
  

    const toggleChartVisibility = () => { // Function to toggle the visibility of the chart
        setShowChart(!showChart);
    };
    
    const hideChart = () => { // Function to hide the chart
        setShowChart(false);
    };

    const toggleTotalQueriesVisibility = () => {
        setShowTotalQueries(!showTotalQueries);
        if (!showTotalQueries) {
          // Fetch and log the total queries when showing the queries
          fetchTotalQueries();
        }
      };

    const revealChatbot = () => {
        // Start the slide-up transition
        const cover = document.getElementById('page-cover');
        if (cover) {
          cover.style.transform = 'translateY(-100%)';
        }
        
        // Wait for transition to complete before removing the cover from the DOM
        setTimeout(() => {
          setCoverVisible(false);
        }, 500); // This should match the duration of your CSS transition
      };

    const fetchTotalQueries = async () => {
        try {
          const response = await fetch('http://127.0.0.1:5000/api/total-queries');
          if (response.ok) {
            const totalQueries = await response.json();
            console.log('Total Queries:', totalQueries);
          } else {
            console.error('Failed to fetch total queries');
          }
        } catch (error) {
          console.error('Error fetching total queries:', error);
        }
      };

      return (
        <>
          {coverVisible && (
            <div id="page-cover" onClick={revealChatbot}>
              <h1>Cyber Seeker Chatbot</h1>
              <h2>Please Click Anywhere on the screen</h2>
              <h3>To access the Chatbot</h3>
            </div>
          )}
          <div className={`chat-container ${coverVisible ? 'hidden' : ''}`}>
            <div id="chat-box" ref={chatBoxRef}>
              {messages.map((message, index) => (
                <div key={index} className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}>
                  {message.text}
                </div>
              ))}
            </div>
            <form onSubmit={sendMessage}>
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask something..."
              />
              <button type="submit">Send</button>
            </form>
            {showTotalQueries && <TotalQueries />}
            <div className="chart-controls">
              <button className="toggle-chart-btn" onClick={toggleChartVisibility}>
                {showChart ? "Back to Chat" : "Searched Topics"}
              </button>
              {showChart && <button className="hide-chart-btn" onClick={hideChart}>Back to Chat</button>}
              <button className="show-queries-btn" onClick={toggleTotalQueriesVisibility}>
                {showTotalQueries ? "Hide User Queries" : "Show User Queries"}
              </button>
            </div>
            {showChart && <TopicDistributionPieChart />}
          </div>
        </>
      );
    };


export default Chatbot;
